typedef Heap MinHeap; struct HNode
{ ElementType *Data; int Size; int Capacity;
}; MinHeap CreateHeap(int MaxSize)
{ MinHeap H = (MinHeap)malloc(sizeof(struct HNode)); H->Data = (ElementType*)malloc(sizeof(ElementType)*MaxSize); H->Size = 0; H->Capacity = MaxSize; H->Data[0] = -10001; return H;
} bool IsFull(MinHeap H)
{ return (H->Size == H->Capacity);
} bool Insert(MinHeap H, ElementType X)
{ if(IsFull(H)) { printf("The heap is full!\n"); return false; } int i = ++H->Size; for(; H->Data[i/2] >= X; i /= 2) { H->Data[i] = H->Data[i/2]; } H->Data[i] = X; return true;
} bool IsEmpty(MinHeap H)
{ return (H->Size == 0);
} ElementType Delete(MinHeap H)
{ if(IsEmpty(H)) { printf("The heap is empty!\n"); return ERROR; } int Parent, Child; ElementType MinItem,X; MinItem = H->Data[1]; X = H->Data[H->Size--]; for(Parent = 1; Parent * 2 <= H->Size; Parent = Child) { Child = Parent * 2; if((Child!=H->Size)&&(H->Data[Child] > H->Data[Child + 1])) Child++; if(X < H->Data[Child]) break; else H->Data[Parent] = H->Data[Child]; } H->Data[Parent] = X; return MinItem;
} int main()
{ int MaxSize = 1001; MinHeap H = CreateHeap(MaxSize); int N,M; scanf("%d %d",&N,&M); for(int i=0; i<N; i++) { int num; scanf("%d",&num); Insert(H,num); } for(int i=0; i<M; i++) { int idx; scanf("%d",&idx); int Fisrt = 1; for(int j=idx; j>=1; j/=2) { if(Fisrt) { printf("%d",H->Data[j]); Fisrt = 0; } else { printf(" %d",H->Data[j]); } } printf("\n"); } return 0;
