Inorder: 1 2 4 6 8 9 BinTree Insert(BinTree BST, ElementType X)
{ if(!BST) { /*若原树为空，生成并返回一个结点的二叉搜索树*/ BST = (BinTree)malloc(sizeof(struct TNode)); BST->Data = X; BST->Left = NULL; BST->Right = NULL; } else /*开始找要插入元素的位置*/ { if(BST->Data < X) BST->Right = Insert(BST->Right, X); else if(BST->Data > X) BST->Left = Insert(BST->Left, X); /*else X已经存在，什么都不做 */ } return BST;
}
BinTree Delete(BinTree BST, ElementType X)
{ Position Tmp; if(!BST) printf("Not Found\n"); else if(X < BST->Data) BST->Left = Delete(BST->Left,X); else if(X > BST->Data) BST->Right = Delete(BST->Right,X); else { if(BST->Left && BST->Right) { Tmp = FindMin(BST->Right); BST->Data = Tmp->Data; BST->Right = Delete(BST->Right,BST->Data); } else { Tmp = BST; if(!BST->Left) BST = BST->Right; else if(!BST->Right) BST = BST->Left; free(Tmp); } } return BST; }
Position Find(BinTree BST,ElementType X)
{ if(!BST) return NULL; else { if(BST->Data == X) return BST; /*查找成功，返回找到结点的地址*/ else if(BST->Data > X) return Find(BST->Left, X); else return Find(BST->Right, X); } return NULL;
} /*迭代、非递归方式*/
Position FindMax(BinTree BST)
{ if(BST) while(BST->Right) BST = BST->Right; return BST;
} Position FindMin(BinTree BST)
{ if(!BST) return NULL; else if(!BST->Left) /*有根结点，但没有左子树，直接返回根*/ return BST; else return FindMin(BST->Left);
}

