int last = 0;
int tail = 0; void ResetVisited()
{ for(int i=0; i<1001; i++) visited[i] = 0;
} int bfs(int v)
{ visited[v] = TRUE; int count = 1; int level = 0; last = v; tail = 0; int queue[1001]; int front = 0; int rear = 0; queue[rear++] = v; while(rear != front) { int temp = queue[front++]; for(int i=1; i<=N; i++) { if(visited[i]==FALSE && G[temp][i]==1) { visited[i] = TRUE; queue[rear++] = i; tail = i; count++; } } if(temp == last) { level++; last = tail; } if(level == 6) break; } return count;
