{ MGraph Graph = (MGraph)malloc(sizeof(struct GNode)); Graph->Nv = VertexNum; Graph->Ne = 0; Vertex V1,V2; for(Vertex V1=0; V1<Graph->Nv; V1++) for(Vertex V2=0; V2<Graph->Nv; V2++) Graph->G[V1][V2] = INFINITY; return Graph;
} void InsertEdge(MGraph Graph, Edge E)
{ Graph->G[E->V1][E->V2] = E->Weight; Graph->G[E->V2][E->V1] = E->Weight;
} MGraph BuildGraph()
{ int Nv; Edge E; scanf("%d", &Nv); MGraph Graph = CreateGraph(Nv); scanf("%d", &(Graph->Ne)); if((Graph->Ne) != 0) { E = (Edge)malloc(sizeof(struct ENode)); for(int i=0; i<Graph->Ne; i++) { scanf("%d %d %d",&E->V1,&E->V2,&E->Weight); E->V1--; E->V2--; InsertEdge(Graph, E); } } return Graph;
