{ WeightType D[MaxVertexNum][MaxVertexNum], MaxDist, MinDist; Vertex Animal, i; Floyd(Graph, D); MinDist = INFINITY; for(i=0; i<Graph->Nv; i++) { MaxDist = FindMaxDist(D, i, Graph->Nv); if(MaxDist == INFINITY) { printf("0\n"); return; } if(MaxDist < MinDist) { MinDist = MaxDist; Animal = i + 1; } } printf("%d %d\n", Animal, MinDist); } int main()
{ MGraph Graph = BuildGraph(); FindAnimal(Graph); return 0;
