};
class AdjacencyLinkGraphic{
public: vector<vector<vertexNode*>> vertexs;//二维数组, 存的值是结点 包含该点的路径和收费 vertexs[i][j] i是起点 j是终点 int cityNum{0}; int highwayNum{0}; int s;//出发城市 int d;//目的城市 AdjacencyLinkGraphic()=default; AdjacencyLinkGraphic(int n_,int m_,int s_,int d_):cityNum{n_},highwayNum{m_},s{s_},d{d_}{}; void build(){ vertexs=vector<vector<vertexNode*>>(cityNum,vector<vertexNode*>(cityNum,nullptr)); int s,d,w,c; for(int i=0;i<highwayNum;i++){ scanf("%d %d %d %d",&s,&d,&w,&c); vertexs[s][d]=new vertexNode{s,d,w,c}; vertexs[d][s]=new vertexNode{d,s,w,c}; } } void dijkstra(){ vector<int> visited(cityNum); visited[s]=1; int u;//起点 的 未访问的 最小权值的 邻接点 // 先将s的访问位置为1, // 1.找到u 然后将u的访问位置为1 // 2.先由起点的最小权值邻接点 u 开始, 寻找起点s 经由 u 到u的邻接点 j 的总权值是否小于 s到j的权值 // 3.如果经由 s 经由 u 到 j 的的路径权值小于s到j的权值 ,就更新s到j的权值为 (s到u的权值)+(u到j的权值) // 循环直到s的邻接点全部遍历完毕,这时就得到了s到任意连通点的最小路径长 和 对应的收费 //直接输出 vertexs[s][d]的路径长 和 收费 for(int i=0;i<cityNum;i++){ int min=999999; for(int j=0;j<cityNum;j++){ if( vertexs[s][j] && !visited[j] && vertexs[s][j]->weight < min){ min= vertexs[s][j]->weight; u = j; } } visited[u]=1; for(int j=0; j<cityNum; j++){ if( vertexs[u][j] && vertexs[u][j]->weight <= 99999 ){ if( vertexs[u][j] && vertexs[s][u] && vertexs[s][j] ){//确认为连通点 if(vertexs[s][j]->weight == vertexs[s][u]->weight + vertexs[u][j]->weight){ if(vertexs[s][j]->charge > vertexs[s][u]->charge + vertexs[u][j]->charge) vertexs[s][j]->charge=vertexs[s][u]->charge+vertexs[u][j]->charge; }else if(vertexs[s][j]->weight > vertexs[s][u]->weight + vertexs[u][j]->weight){ //更新由s到j的最小权值和对应的路费 vertexs[s][j]->weight = vertexs[s][u]->weight + vertexs[u][j]->weight; vertexs[s][j]->charge = vertexs[s][u]->charge + vertexs[u][j]->charge; } } } } } cout << vertexs[s][d]->weight <<" "<< vertexs[s][d]->charge<<endl; }
};
int main(){ int n,m,s,d; cin >> n >> m >> s >> d ; AdjacencyLinkGraphic ALG{n,m,s,d}; ALG.build(); ALG.dijkstra(); return 0;
