/* 快速排序 */ ElementType Median3( ElementType A[], int Left, int Right )
{ int Center = (Left+Right) / 2; if ( A[Left] > A[Center] ) Swap( &A[Left], &A[Center] ); if ( A[Left] > A[Right] ) Swap( &A[Left], &A[Right] ); if ( A[Center] > A[Right] ) Swap( &A[Center], &A[Right] ); /* 此时A[Left] <= A[Center] <= A[Right] */ Swap( &A[Center], &A[Right-1] ); /* 将基准Pivot藏到右边*/ /* 只需要考虑A[Left+1] … A[Right-2] */ return A[Right-1]; /* 返回基准Pivot */
} void Qsort( ElementType A[], int Left, int Right )
{ /* 核心递归函数 */ int Pivot, Cutoff, Low, High; if ( Cutoff <= Right-Left ) { /* 如果序列元素充分多，进入快排 */ Pivot = Median3( A, Left, Right ); /* 选基准 */ Low = Left; High = Right-1; while (1) { /*将序列中比基准小的移到基准左边，大的移到右边*/ while ( A[++Low] < Pivot ) ; while ( A[--High] > Pivot ) ; if ( Low < High ) Swap( &A[Low], &A[High] ); else break; } Swap( &A[Low], &A[Right-1] ); /* 将基准换到正确的位置 */ Qsort( A, Left, Low-1 ); /* 递归解决左边 */ Qsort( A, Low+1, Right ); /* 递归解决右边 */ } else InsertionSort( A+Left, Right-Left+1 ); /* 元素太少，用简单排序 */ } void QuickSort( ElementType A[], int N )
{ /* 统一接口 */ Qsort( A, 0, N-1 );
}

