} void MSort(ElementType A[], ElementType TmpA[], int L, int RightEnd)
{ int Center; if(L < RightEnd) { Center = (L + RightEnd)/2; MSort(A, TmpA, L, Center); MSort(A, TmpA, Center+1, RightEnd); Merge(A, TmpA, L, Center+1, RightEnd); }
