{ LPHASH pHash = (LPHASH)malloc(sizeof(struct hashTable)); assert(pHash); pHash->divisor = divisor; pHash->curSize = 0; pHash->table = (LPDATA*)malloc(sizeof(LPDATA) * pHash->divisor); assert(pHash->table); for(int i=0; i < pHash->divisor; i++) { pHash->table[i] = NULL; } return pHash;
} int searchCorrectPos(LPHASH pHash, int key)
{ int Pos = key % pHash->divisor; int curPos = Pos; do { if(pHash->table[curPos] == NULL || pHash->table[curPos]->key == key) return curPos; curPos = (curPos + 1) % pHash->divisor; }while(curPos != Pos); return curPos;
} void insertHash(LPHASH pHash, DATA data)
{ int pos = searchCorrectPos(pHash, data.key); if(pHash->table[pos] == NULL) { pHash->table[pos] = (LPDATA)malloc(sizeof(DATA)); assert(pHash->table[pos]); memcpy(pHash->table[pos], &data, sizeof(DATA)); pHash->curSize++; } else { if(pHash->table[pos]->key == data.key) { strcpy(pHash->table[pos]->element, data.element); //遇到冲突，覆盖相同key } else { printf("表满了，无法插入！\n"); return; } }
