#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <assert.h> typedef struct pair
{ int key; char element[20];
}DATA, *LPDATA; typedef struct Node
{ DATA data; struct Node *Next;
}NODE, *LPNODE; LPNODE createNode(DATA data)
{ LPNODE newNode = (LPNODE)malloc(sizeof(NODE)); newNode->data = data; newNode->Next = NULL; return newNode;
} typedef struct skipListNode
{ DATA data; LPNODE fisrtNode; struct skipListNode *Next;
}SLNODE, *LPSLNODE; LPSLNODE createSkipListNode(DATA data)
{ LPSLNODE newNode = (LPSLNODE)malloc(sizeof(SLNODE)); newNode->data = data; newNode->fisrtNode = NULL; newNode->Next = NULL; return newNode;
} typedef struct Hash
{ LPSLNODE headNode; int curSize; int divisor;
}HASH, *LPHASH; LPHASH createHash(int divisor)
{ LPHASH pHash = (LPHASH)malloc(sizeof(HASH)); assert(pHash); pHash->curSize = 0; pHash->divisor = divisor; pHash->headNode = NULL; return pHash;
} void insertHash(LPHASH pHash, DATA data)
{ int dataHashPos = data.key % pHash->divisor; LPSLNODE newSkipNode = createSkipListNode(data); //第一次插入 if(pHash->headNode == NULL) { pHash->headNode = newSkipNode; pHash->curSize++; } else { //纵向是要有序的 LPSLNODE pMove = pHash->headNode; LPSLNODE prePmove = NULL; //表头元素的地址大于要插入元素的地址，用头插法 if(pMove->data.key % pHash->divisor > dataHashPos) { newSkipNode->Next = pHash->headNode; pHash->headNode = newSkipNode; pHash->curSize++; } else { //向下找 while(pMove!=NULL && pMove->data.key%pHash->divisor < dataHashPos) { prePmove = pMove; pMove = prePmove->Next; } //结果：找到、没找到 //找到的话，单独分析等于的情况，等于就是哈希冲突 if(pMove!=NULL && pMove->data.key%pHash->divisor == dataHashPos) { //相同键采用覆盖方式 //不同键、相同哈希地址插入横向链表 if(pMove->data.key == data.key) { strcpy(pMove->data.element, data.element); } else { LPNODE newNode = createNode(data); LPNODE ppMove = pMove->fisrtNode; //横向链表的插入 if(ppMove == NULL) { newNode->Next = pMove->fisrtNode; pMove->fisrtNode = newNode; pHash->curSize++; } else { //横向处理相同key的问题 while(ppMove != NULL && ppMove->data.key != data.key) { ppMove = ppMove->Next; } if(ppMove == NULL) { //表头法插入 newNode->Next = pMove->fisrtNode; pMove->fisrtNode = newNode; pHash->curSize++; } else { //相同则覆盖 strcpy(ppMove->data.element, data.element); } } } } else { prePmove->Next = newSkipNode; newSkipNode->Next = pMove; pHash->curSize++; } } }
} void printHash(LPHASH pHash)
{ LPSLNODE pMove = pHash->headNode; while(pMove != NULL) { printf("%d:%s ",pMove->data.key, pMove->data.element); LPNODE ppMove = pMove->fisrtNode; while(ppMove != NULL) { printf("%d:%s ",ppMove->data.key, ppMove->data.element); ppMove = ppMove->Next; } pMove = pMove->Next; printf("\n"); }
} int main()
{ DATA array[3] = {29,"Young",35,"蓬蒿人",39,"哦哦哦"}; LPHASH pHash = createHash(10); for(int i=0; i<3; i++) { insertHash(pHash,array[i]); } printHash(pHash); return 0;
} 35:蓬蒿人
