{ char string[1000005]; scanf("%s",string); int N; scanf("%d",&N); for(int i=0; i<N; i++) { char pattern[100005]; scanf("%s",pattern); Position p = strstr(string, pattern); if (p==NotFound) printf("Not Found\n"); else printf("%s\n", p); } return 0;
}

