{ List PtrL; PtrL=(List)malloc(sizeof(struct LNode)); PtrL->Last=-1; return PtrL;
}
Position Find( List L, ElementType X )
{ for(Position i=0;i<=L->Last+1;i++) { if(L->Data[i]==X) return i; } return ERROR; /*int i=0; while(i<L->Last&&L->Data[i]!=X) i++; if(i>L->Last) return ERROR;//如果没找到，返回ERROR else return i;//找到后返回的是存储位置*/
}
bool Insert( List L, ElementType X, Position P )
{ if(L->Last==MAXSIZE-1) { printf("FULL"); return false; } if(P<0||P>L->Last+1||P>=MAXSIZE) { printf("ILLEGAL POSITION"); return false; } for(int j=L->Last;j>=P;j--) { L->Data[j+1]=L->Data[j]; } L->Data[P]=X; L->Last++; return true; }
bool Delete( List L, Position P )
{ if(P<0||P>L->Last) { printf("POSITION %d EMPTY",P); return false; } if(P!=L->Last) { for(int j=P;j<L->Last;j++) L->Data[j]=L->Data[j+1]; } L->Last--; return true;
}

