{ while(L) { if(L->Data==X) return L; L = L->Next; } return ERROR;
}
List Insert( List L, ElementType X, Position P )
{ List head = L; List p = (List)malloc(sizeof(List)); p->Data = X; p->Next = NULL; if(L==P) { p->Next = L; return p; } while(L) { if(P==L->Next) { p->Next = L->Next; L->Next = p; return head; } L=L->Next; } printf("Wrong Position for Insertion\n"); return ERROR; }
