{ int i=0; while(i<=PtrL->Last&&PtrL->Data[i]!=X) i++; if(i>PtrL->Last) return -1;//如果没找到，返回-1 else return i;//找到后返回的是存储位置（下标）
} /*插入（第i（1<=i<=n+1）个位置上插入一个值为X的新元素*/ void Insert(ElementType X,int i,List PtrL) /*i是序数，为下标+1，不是下标*/
{ int j; if(PtrL->Last==MAXSIZE-1) /*表空间已满，不能插入*/ { printf("表满"); return; } if(i<1||i>PtrL->Last+2) /*检查插入位置的合法性*/ { printf("位置不合法"); return; } for(j=PtrL->Last;j>=i-1;j--) PtrL->Data[j+1]=PtrL->Data[j]; /*将ai~an倒序向后移动*/ PtrL->Data[i-1]=X; /*新元素插入*/ PtrL->Last++; /*Last仍指向最后一个元素*/ return;
} /*删除：删除表的第i（1<=i<=n）个位置上的元素*/ void Delete(int i,List PtrL)
{ int j; if(i<1||i>PtrL->Last+1) /*检查是否为空表及删除位置的合法性*/ { printf("不存在第%d个元素",i); return; } for(j=i;j<=PtrL->Last;j++) PtrL->Data[j-1]=PtrL->Data[j]; /*将a(i+1)~an顺序向前移动*/ PtrL->Last--; /*Last仍指向最后一个元素*/ return;
} 《大话数据结构》给出的顺序存储的链表实例： #include <bits/stdc++.h>
using namespace std; #define MAXSIZE 100
#define OK 1
#define ERROR 0
#define OVERFLOW -2 //这样就为 `OVERFLOW` 定义了一个值为 `-2` 的常量。 typedef int ElemType;
typedef int Status; typedef struct
{ ElemType *elem; int length;
}SqList; //线性表的初始化
Status InitList(SqList &L)
{ L.elem=new ElemType[MAXSIZE]; if(!L.elem) exit(OVERFLOW); L.length=0; return OK;
}
//线性表的取值
Status GetElem(SqList L,int i,ElemType &e)
{ if(i<1||i>L.length) return ERROR; e=L.elem[i-1]; return OK;
}
//查找元素
int LocateElem(SqList L,ElemType e)
{ for(int i=0;i<L.length;i++) { if(L.elem[i]==e) return i+1; } return 0;
}
//线性表插入元素
Status ListInsert(SqList &L,int i,ElemType e)
{ if(i<1||i>L.length+1) return ERROR; if(L.length==MAXSIZE) return ERROR; for(int j=L.length-1;j>=i-1;j--) { L.elem[j+1]=L.elem[j]; } L.elem[i-1]=e; L.length++; return OK;
}
//线性表删除元素
Status ListDelete(SqList &L,int i)
{ if(i<1||i>L.length) return ERROR; for(int j=i;j<=L.length-1;j++) L.elem[j-1]=L.elem[j]; L.length--; return OK;
}
