{ List p; int i; *L=(List)malloc(sizeof(struct LNode)); (*L)->Next=NULL; for(i=0;i<n;i++) { p=(List)malloc(sizeof(struct LNode)); p->Data=m[i]; p->Next=(*L)->Next; (*L)->Next=p; }
