{ if(L1 == NULL || L2 == NULL) return (L1 == NULL)?L2:L1; List head = (List)malloc(sizeof(struct Node)); List cur1 = L1->Next; List cur2 = L2->Next; List pre = head; while(cur1 != NULL && cur2 != NULL) { if(cur1->Data <= cur2->Data) { pre->Next = cur1; cur1 = cur1->Next; } else { pre->Next = cur2; cur2 = cur2->Next; } pre = pre->Next; } pre->Next = (cur1 != NULL)?cur1:cur2; L1->Next = NULL; L2->Next = NULL; return head; }

